import React from 'react';
import axios from 'axios';

class NameForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      fname: '',
      lname: '',
      email: '',
      phone: '',
      message: ''
    };
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }
  async handleFormSubmit(event) {
    event.preventDefault();
    validate();
    const { fname, lname, email, phone, message } = this.state;
    alert('Thank you ' + this.state.fname + ' I will get back to you soon.');
    const form = await axios.post('/api/form', {
      fname,
      lname,
      email,
      phone,
      message
    });
  }

  render() {
    return (
      <section className='contact'>
        <div className='container'>
          <div>
            <h3>Let's Work Together!</h3>
          </div>
          <form action='/' onSubmit={this.handleFormSubmit} id='form'>
            <ul className='flex-outer'>
              <li>
                <label htmlFor='first-name'>First Name</label>
                <input
                  type='text'
                  id='first-name'
                  placeholder='Enter your first name here'
                  value={this.state.fname}
                  onChange={e => this.setState({ fname: e.target.value })}
                />
              </li>
              <li>
                <label htmlFor='last-name'>Last Name</label>
                <input
                  type='text'
                  id='last-name'
                  value={this.state.lname}
                  onChange={e => this.setState({ lname: e.target.value })}
                  placeholder='Enter your last name here'
                />
              </li>
              <li>
                <label htmlFor='email'>Email</label>
                <input
                  type='email'
                  id='email'
                  placeholder='Enter your email here'
                  value={this.state.email}
                  onChange={e => this.setState({ email: e.target.value })}
                />
              </li>
              <li>
                <label htmlFor='phone'>Phone</label>
                <input
                  type='tel'
                  id='phone'
                  placeholder='Enter your phone here'
                  value={this.state.phone}
                  onChange={e => this.setState({ phone: e.target.value })}
                />
              </li>
              <li>
                <label htmlFor='message'>Message</label>
                <textarea
                  rows='6'
                  id='message'
                  placeholder='Enter your message here'
                  onChange={e => this.setState({ message: e.target.value })}
                  value={this.state.message}
                ></textarea>
              </li>

              <li>
                <button
                  onClick={e => this.handleFormSubmit(e)}
                  value='Submit'
                  type='submit'
                >
                  Submit
                </button>
              </li>
            </ul>
            <div>
              {this.state.mailSent && (
                <div>
                  <h1 style={{ color: 'white' }}>Thank you for submitting!</h1>
                </div>
              )}
            </div>
          </form>
        </div>
      </section>
    );
  }
}

//custom react hook
function validate() {
  let errors = {};

  if (!state.email) {
    errors.email = 'Email address is required';
  }
}

export default NameForm;
