import React, { Component } from 'react';

import axios from 'axios';

const validEmailRegex = RegExp(
  /^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i
);

class NameForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      fullName: '',
      email: '',
      phone: '',
      message: '',
      errors: {
        fullName: '',
        email: '',
        phone: '',
        message: ''
      }
    };
  }

  handleChange = event => {
    event.preventDefault();
    const { name, value } = event.target;
    let errors = this.state.errors;

    switch (name) {
      case 'fullName':
        errors.fullName =
          value.length < 5 ? 'Full Name must be 5 characters long!' : '';
        break;
      case 'email':
        errors.email = validEmailRegex.test(value) ? '' : 'Email is not valid!';
        break;
      case 'message':
        errors.message =
          value.length < 10 ? 'Message must be 10 characters long!' : '';
        break;
      default:
        break;
    }

    this.setState({ errors, [name]: value }, () => {
      console.log(errors);
    });
  };
  async handleFormSubmit(event) {
    event.preventDefault();
    const { fullName, email, phone, message } = this.state;
    alert('Thank you ' + this.state.fullName + ' I will get back to you soon.');
    const form = await axios.post('/api/form', {
      fullName,
      email,
      phone,
      message
    });
  }

  render() {
    return (
      <section className='contact'>
        <div className='container'>
          <div>
            <h3>Let's Work Together!</h3>
          </div>
          <form action='/' onSubmit={this.handleFormSubmit} id='form'>
            <ul className='flex-outer'>
              <li>
                <label htmlFor='first-name'>Full Name</label>
                <input
                  type='text'
                  id='full-name'
                  placeholder='Enter your first name here'
                  value={this.state.fullName}
                  onChange={e => this.setState({ fullName: e.target.value })}
                />
              </li>

              <li>
                <label htmlFor='email'>Email</label>
                <input
                  type='email'
                  required
                  id='email'
                  placeholder='Enter your email here'
                  value={this.state.email}
                  onChange={e => this.setState({ email: e.target.value })}
                />
              </li>
              <li>
                <label htmlFor='phone'>Phone</label>
                <input
                  type='tel'
                  id='phone'
                  placeholder='Enter your phone here'
                  value={this.state.phone}
                  onChange={e => this.setState({ phone: e.target.value })}
                />
              </li>
              <li>
                <label htmlFor='message'>Message</label>
                <textarea
                  rows='6'
                  required
                  id='message'
                  placeholder='Enter your message here'
                  onChange={e => this.setState({ message: e.target.value })}
                  value={this.state.message}
                ></textarea>
              </li>

              <li>
                <button
                  onClick={e => this.handleFormSubmit(e)}
                  value='Submit'
                  type='submit'
                >
                  Submit
                </button>
              </li>
            </ul>
            <div>
              {this.state.mailSent && (
                <div>
                  <h1 style={{ color: 'white' }}>Thank you for submitting!</h1>
                </div>
              )}
            </div>
          </form>
        </div>
      </section>
    );
  }
}

export default NameForm;
