window.onload = () => {
  function SmokeEffect() {
    if (
      navigator.userAgent.search('Safari') >= 0 &&
      navigator.userAgent.search('Chrome') < 0
    ) {
      function removeColor() {
        const element = document.getElementById('homepage');
        element.classList.add('safari');
        element.classList.remove('homepage');
        alert('safari');
      }
      removeColor();
    } else {
      function AddColor() {
        const color = document.getElementById('homepage');
        color.classList.add('homepage');
      }
      AddColor();
    }
  }
  function Smoke() {
    document
      .getElementById('smoke')
      .addEventListener('ended', myHandler, false);
    function myHandler(e) {
      if (!e) {
        e = window.event;
      }

      document.getElementById('smoke').style.display = 'none';
      document.getElementById('smokeEnd').style.display = 'block';
    }

    SmokeEffect();
    Smoke();
  }
};
