import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import * as serviceWorker from './serviceWorker';
import 'react-mdl/extra/material.css';
import 'react-mdl/extra/material.js';
import { BrowserRouter } from 'react-router-dom';

ReactDOM.render(
  <BrowserRouter>
    <App />
  </BrowserRouter>,
  document.getElementById('root')
);

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();

// Smoke Animation

function Smoke() {
  document.getElementById('smoke').addEventListener('ended', myHandler, false);
  function myHandler(e) {
    if (!e) {
      e = window.event;
    }
    // What you want to do after the event
    document.getElementById('smoke').style.display = 'none';
    document.getElementById('smokeEnd').style.display = 'block';
  }

  // ---Smoke Video Issue with color effect in Safari---

  if (
    navigator.userAgent.search('Safari') >= 0 &&
    navigator.userAgent.search('Chrome') < 0
  ) {
    document.getElementById('smoke').style.display = 'none';
    alert(
      'You are using Safari, please try Chrome, FireFox or Torch to see smoke animation.'
    );
  }
}
Smoke();
